import React, { useState } from "react";
import './ImageMenu.scss';

import image from '../../assets/images/image.jpg';


const ImageMenu = () => {

    const [ list, setList ] = useState([]);

    const getList = async () => {
        const request = await fetch('https://via.placeholder.com/600/d32776')
    }

    useEffect(() => {
        getList();
    }, [])


    return (
        <div className="confirm-wrapper">
            <div className="confirm-menu">
                <div className="confirm-menu__header">
                    <span className="confirm-menu__header__title">Image Gallery</span>
                    <div className="confirm-menu__header__close-btn">x</div>
                </div>
                <div className="confirm-menu__content">
                    <div className="confirm-menu__content__image-src">
                        <label>
                            Image URL
                            <input type="url" defaultValue={'images/tralala'}></input>
                        </label>
                        <label>
                            Image alt
                            <input type="text" defaultValue={'Image Alternative'}></input>
                        </label>
                        <img src={image}></img>
                    </div>
                    <div className="confirm-menu__content__images">
                        {imagesList()}
                        <ul>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                            <li><img alt='картинка 1'></img></li>
                        </ul>
                    </div>
                </div>
                <div className="confirm-menu__bottom-btns">
                    <button className="upload-btn" type="button">UPLOAD IMAGE</button>
                    <div className="confirm-menu__bottom-btns__confirm">
                        <button className="cancel-btn" type="button">CANCEL</button>
                        <button className="apply-btn" type="button">APPLY</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ImageMenu;
